parts.text:
[
  {
    "id": "window_icon",
    "type": "icon",
    "text": "LibreOffice Writer icon",
    "region": "top-bar",
    "actions": ["onClick"]
  },
  {
    "id": "window_title",
    "type": "label",
    "text": "Sem título 1 — LibreOffice Writer",
    "region": "top-bar"
  },
  {
    "id": "minimize_button",
    "type": "button",
    "text": "Minimizar",
    "region": "top-bar",
    "actions": ["onClick"]
  },
  {
    "id": "maximize_restore_button",
    "type": "button",
    "text": "Maximizar/Restaurar",
    "region": "top-bar",
    "actions": ["onClick"]
  },
  {
    "id": "close_button",
    "type": "button",
    "text": "Fechar",
    "region": "top-bar",
    "actions": ["onClick"]
  },
  {
    "id": "menu_arquivo",
    "type": "menu-item",
    "text": "Arquivo",
    "region": "menu-bar",
    "actions": ["onClick", "onHover"]
  },
  {
    "id": "menu_editar",
