  {
    "id": "menu_estilos",
    "type": "menu-item",
    "text": "Estilos",
    "region": "menu-bar",
    "actions": ["onClick", "onHover"]
  },
  {
    "id": "menu_tabela",
    "type": "menu-item",
    "text": "Tabela",
    "region": "menu-bar",
    "actions": ["onClick", "onHover"]
  },
  {
    "id": "menu_formulario",
