  },
  {
    "id": "menu_ferramentas",
    "type": "menu-item",
    "text": "Ferramentas",
    "region": "menu-bar",
    "actions": ["onClick", "onHover"]
  },
  {
    "id": "menu_janela",
    "type": "menu-item",
    "text": "Janela",
    "region": "menu-bar",
    "actions": ["onClick", "onHover"]
  },
  {
    "id": "menu_ajuda",
    "type": "menu-item",
    "text": "Ajuda",
    "region": "menu-bar",
    "actions": ["onClick", "onHover"]
  },
  {
    "id": "toolbar_new_document",
    "type": "button",
    "text": "Novo documento",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "document-new"
    }
  },
  {
    "id": "toolbar_open_document",
    "type": "button",
    "text": "Abrir documento",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "document-open"
    }
  },
  {
    "id": "toolbar_save_document",
    "type": "button",
    "text": "Salvar documento",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "document-save"
    }
  },
  {
    "id": "toolbar_print",
    "type": "button",
    "text": "Imprimir",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "print"
    }
  },
  {
    "id": "toolbar_print_preview",
    "type": "button",
    "text": "Visualizar impressão",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "print-preview"
    }
  },
  {
    "id": "toolbar_cut",
    "type": "button",
    "text": "Recortar",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "cut"
    }
  },
  {
    "id": "toolbar_copy",
    "type": "button",
    "text": "Copiar",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "copy"
    }
  },
  {
    "id": "toolbar_paste",
    "type": "button",
    "text": "Colar",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "paste"
    }
  },
  {
    "id": "toolbar_undo",
    "type": "button",
    "text": "Desfazer",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "undo"
    }
  },
  {
    "id": "toolbar_redo",
    "type": "button",
    "text": "Refazer",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "redo"
    }
  },
  {
    "id": "toolbar_paragraph_style",
    "type": "dropdown",
    "text": "Estilo de parágrafo Padrão",
    "region": "toolbar",
    "actions": ["onChange", "onClick"],
    "meta": {
      "current_value": "Estilo de parágrafo Padrão"
    }
  },
  {
    "id": "toolbar_font_name",
    "type": "dropdown",
    "text": "Arial",
    "region": "toolbar",
    "actions": ["onChange", "onClick"],
    "meta": {
      "current_value": "Arial"
    }
  },
  {
    "id": "toolbar_font_size",
    "type": "dropdown",
    "text": "12 pt",
    "region": "toolbar",
    "actions": ["onChange", "onClick"],
    "meta": {
      "current_value": "12 pt"
    }
  },
  {
    "id": "toolbar_bold",
    "type": "button",
    "text": "Negrito",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "bold"
    }
  },
  {
    "id": "toolbar_italic",
    "type": "button",
    "text": "Itálico",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "italic"
    }
  },
  {
    "id": "toolbar_underline",
    "type": "button",
    "text": "Sublinhado",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "underline"
    }
  },
  {
    "id": "toolbar_font_color",
    "type": "button",
    "text": "Cor da fonte",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "font-color"
    }
  },
  {
    "id": "toolbar_highlight_color",
    "type": "button",
    "text": "Cor de destaque",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "highlight-color"
    }
  },
  {
    "id": "toolbar_align_left",
    "type": "button",
    "text": "Alinhar à esquerda",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "align-left"
    }
  },
  {
    "id": "toolbar_align_center",
    "type": "button",
    "text": "Centralizar",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "align-center"
    }
  },
  {
    "id": "toolbar_align_right",
    "type": "button",
    "text": "Alinhar à direita",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "align-right"
    }
  },
  {
    "id": "toolbar_justify",
    "type": "button",
    "text": "Justificar",
    "region": "toolbar",
    "actions": ["onClick"],
    "meta": {
      "icon": "justify"
    }
  },
  {
    "id": "toolbar_insert_table",
    "type": "button",
    "text": "Inserir Tabela",
    "region": "toolbar",
    "state": "active",
    "actions": ["onClick"],
    "meta": {
      "icon": "insert-table"
    }
  },
  {
    "id": "table_insert_popup",
    "type": "popup",
    "text": null,
    "region": "main-content",
    "state": "open",
    "meta": {
      "title": "Tabela"
    },
    "components": [
      {
        "id": "table_grid_selector",
        "type": "grid-selector",
        "text": null,
        "region": "table-insert-popup",
        "actions": ["onHover", "onClick"],
        "meta": {
          "description": "Grid para selecionar dimensões da tabela (linhas x colunas)",
          "max_dimensions": "10x10"
        }
      },
      {
        "id": "table_other_options_button",
        "type": "button",
        "text": "Outras opções...",
        "region": "table-insert-popup",
        "actions": ["onClick"]
      }
    ]
  },
  {
    "id": "horizontal_ruler",
    "type": "ruler",
    "text": null,
    "region": "main-content"
  },
  {
    "id": "document_editor_area",
    "type": "text-editor",
    "text": null,
    "region": "main-content",
    "state": "editable",
    "actions": ["onInput", "onFocus"]
  },
  {
    "id": "vertical_scrollbar_main_content",
    "type": "scrollbar",
    "text": null,
    "region": "main-content",
    "actions": ["onScroll"]
  },
  {
    "id": "sidebar_properties_icon",
    "type": "icon-button",
    "text": "Propriedades",
    "region": "sidebar",
    "actions": ["onClick"]
  },
  {
    "id": "sidebar_styles_icon",
    "type": "icon-button",
    "text": "Estilos",
    "region": "sidebar",
    "actions": ["onClick"]
  },
  {
    "id": "sidebar_gallery_icon",
    "type": "icon-button",
    "text": "Galeria",
    "region": "sidebar",
    "actions": ["onClick"]
  },
  {
    "id": "sidebar_navigator_icon",
    "type": "icon-button",
    "text": "Navegador",
    "region": "sidebar",
    "actions": ["onClick"]
  },
  {
    "id": "sidebar_styles_formatting_icon",
    "type": "icon-button",
    "text": "Estilos e Formatação",
    "region": "sidebar",
    "actions": ["onClick"]
  },
  {
    "id": "sidebar_find_replace_icon",
    "type": "icon-button",
    "text": "Localizar e Substituir",
    "region": "sidebar",
    "actions": ["onClick"]
  },
  {
    "id": "vertical_scrollbar_sidebar",
    "type": "scrollbar",
    "text": null,
    "region": "sidebar",
    "actions": ["onScroll"]
  },
  {
    "id": "status_bar_page_count",
    "type": "label",
    "text": "Página 1 de 1",
    "region": "status-bar"
  },
  {
    "id": "status_bar_word_count",
    "type": "label",
    "text": "0 palavra",
    "region": "status-bar"
  },
  {
    "id": "status_bar_character_count",
    "type": "label",
    "text": "0 caractere",
    "region": "status-bar"
  },
  {
    "id": "status_bar_page_style",
    "type": "label",
    "text": "Estilo de página Padrão",
    "region": "status-bar"
  },
  {
    "id": "status_bar_language",
    "type": "label",
    "text": "Português (Brasil)",
    "region": "status-bar",
    "actions": ["onClick"]
  },
  {
    "id": "status_bar_view_single_page",
    "type": "button",
    "text": "Visualização de página única",
    "region": "status-bar",
    "actions": ["onClick"],
    "meta": {
      "icon": "view-single-page"
    }
  },
  {
    "id": "status_bar_view_multiple_pages",
    "type": "button",
    "text": "Visualização de múltiplas páginas",
    "region": "status-bar",
    "actions": ["onClick"],
    "meta": {
      "icon": "view-multiple-pages"
    }
  },
  {
    "id": "status_bar_view_book",
    "type": "button",
    "text": "Visualização de livro",
    "region": "status-bar",
    "actions": ["onClick"],
    "meta": {
      "icon": "view-book"
    }
  },
  {
    "id": "status_bar_zoom_out",
    "type": "button",
    "text": "Diminuir zoom",
    "region": "status-bar",
    "actions": ["onClick"],
    "meta": {
      "icon": "zoom-out"
    }
  },
  {
    "id": "status_bar_zoom_slider",
    "type": "slider",
    "text": null,
    "region": "status-bar",
    "actions": ["onChange"],
    "meta": {
      "min": 20,
      "max": 600,
      "current_value": 100
    }
  },
  {
    "id": "status_bar_zoom_in",
    "type": "button",
    "text": "Aumentar zoom",
    "region": "status-bar",
    "actions": ["onClick"],
    "meta": {
      "icon": "zoom-in"
    }
  },
  {
    "id": "status_bar_zoom_percentage",
    "type": "label",
    "text": "100%",
    "region": "status-bar",
    "actions": ["onClick"]
  }
